import React from 'react';
import PropTypes from 'prop-types';
import { Marker } from 'react-naver-maps'; // 패키지 불러오기
import './marker.css';


const CustomMarker = () => {
	const navermaps = window.naver.maps;
	/*
	return ( 
		{
      content: [
       '<div class="cafe_marker">',
				 '<div class="map_group _map_group">',
					 '<div class="map_marker _marker tvhp tvhp_big">',
						'<span class="ico _icon"></span>',
						'<span class="shd"></span>',
					'</div>',
				'</div>',
			'</div>'
			].join(''),

	 		size: new navermaps.Size(38, 58),
		 	anchor: new navermaps.Point(19, 58)
		})
	*/

	return (
	{
        url: 'https://raw.githubusercontent.com/nero96in/coronamap_deploy/master/main/static/main/image/red/pharmacy-marker.png',
        size: new navermaps.Size(38, 58),
        origin: new navermaps.Point(0, 0),
        anchor: new navermaps.Point(19, 58)
    }
	)
}

function CafeMarker({lat, lng}) {

	return (  
		<Marker
			position={{ lat: lat, lng: lng }}
			icon={CustomMarker()}
		/>
	)
}


CafeMarker.propTypes = {
	lat: PropTypes.number.isRequired,
	lng: PropTypes.number.isRequired
}

export default CafeMarker;
