import React from 'react';
import './App.css';
import Map from './components/Map'; 
import { RenderAfterNavermapsLoaded} from 'react-naver-maps'; // 패키지 불러오기

class App extends React.Component { 
	constructor(props) {

		super(props);
		console.log("BORN!");

	}
	state = {
		count: 0,

		isLoading: true
	};

	add = () => {
		console.log("add");
		this.setState(current => ({
			count: current.count +1
		}));
	};

	minus= () => {
		console.log("add");
		this.setState(current => ({
			count: current.count -1
		}));
	};


	componentDidMount(){
		console.log('component rendered!');
		setTimeout(() => {
			this.setState({ isLoading: false});
		}, 2000);

	}
	componentDidUpdate(){
		console.log('i just updated!');
	}
	render() {
		console.log('i am rendering');
		const { isLoading } = this.state;

		return (
			<RenderAfterNavermapsLoaded
				ncpClientId={'r7dvstqo0s'} // 자신의 네이버 계정에서 발급받은 Client ID
				error={<p>Maps Load Error</p>}
				loading={<p>Maps Loading...</p>}
			>
				<div id="container">
					<div className="menu_div">
						<img src="/images/ui/menu/hamburger.svg" className="menu_hamburger"></img>
						<img src="/images/ui/menu/activate.svg" className="menu_activate"></img>
					</div>

					<div className="info_div">
						<p className="info_p"> 혼잡도 </p>	
						<img src="/images/ui/info/green.svg" className="info_color"></img>
						<img src="/images/ui/info/busy1.svg"></img>
						<img src="/images/ui/info/yellow.svg" className="info_color"></img>
						<img src="/images/ui/info/busy2.svg"></img>
						<img src="/images/ui/info/red.svg" className="info_color"></img>
						<img src="/images/ui/info/busy3.svg"></img>
					</div>
					<Map/>

					<div className="search_group">
						<div className="search_bar">
							<div className="search_content search_btn_wrap">
								<button type="button" class="search_btn"> </button>
							</div>
							<div className="search_content input_wrap">
								<input type="text" className="search_input" placeholder="위치를 검색해주세요"></input>
							</div>

						</div>
						<div className="filter_group">
							<div>

							</div>
							<div></div>
							<div></div>

							<button type="button" class="search_btn"> </button>





						</div>
						/*
						<div>
							{isLoading ? "Loading" : "Done"}
							<h1>{this.state.count}</h1>
						</div>
						<ul>
							<li onClick={this.add}>Cafe List</li>
							<li onClick={this.minus}>Cafe Find</li>
						</ul>
					</div>
						*/
				</div>
			</RenderAfterNavermapsLoaded>
/*
			<Router>
				<Switch>
					<Router path="/index"/>
					<Router path="/" />
				</Switch>
			</Router>
*/
		);
	}
}

export default App;
