import React from 'react';
import axios from 'axios';
import { NaverMap, Marker } from 'react-naver-maps'; // 패키지 불러오기




function CafeMarker({lat, lng}) {

	return (  
		<Marker
			position={{ lat: lat, lng: lng }}
		/>
	)
}



class Map extends React.Component {

  constructor(props) {

		super(props);
		console.log("BORN NAVER!")
	}

	state = {
		cafes: [],
		isLoading: true
	};

	getCafes = async() => {
		const {data} = await axios.post("/getcafes");
		this.setState(current => ({
			cafes: data, 
			isLoading: false
		}));
	}

	async	componentDidMount(){
		await this.getCafes()
		console.log("DID mount!");
	}

	componentDidUpdate(){

	}

	render() {
  	//const navermaps = window.naver.maps; // 혹은 withNavermaps hoc을 사용
   	//const latlng = new navermaps.LatLng(37.3595704, 127.105399)


		const cafes = this.state.cafes;

  	return (


				<NaverMap
					mapDivId={'maps-getting-started-uncontrolled'} // default: react-naver-map
					style={{
						width: '100%', // 네이버지도 가로 길이
						height: '85vh' // 네이버지도 세로 길이
					}}
					defaultCenter={{ lat: 37.554722, lng: 126.970833 }} // 지도 초기 위치
					defaultZoom={13} // 지도 초기 확대 배율
				>

					{cafes.map((cafe, index) => (
						<CafeMarker lat={cafe.lat} lng={cafe.lon} key={index}/>
					))}


				</NaverMap>
		);
	}
}

export default Map;
