import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { NaverMap } from 'react-naver-maps'; // 패키지 불러오기
import CafeMarker from './Cafemarker';

const useAxios = (url) => {
	const [state, setState] = useState({
		data: null,
		loading: true,
		error: null
	});

	const [trigger, setTrigger] = useState(0);

	const refetch = () => { 
		setState({
			loading: true
		});
		setTrigger(Date.now());
	};

	const getData = async () => {
		const result = await axios.post(url);
		setState({
			data: result.data,
			loading: false,
			error: null
		});
		if (result.error) {
			setState({data:result.data, loading:false, error:result.error});
		}
	};

	useEffect(() => { getData(); }, [trigger]);
	if (!url) {
		return;
	}
	return {state, refetch};
};



const Map = () => {
	const { state, refetch } = useAxios("/getcafes");
	const {data, loading, error} = state;

	let cafes;
	if (data)	{
		cafes = data[0];
	}
	else {
		cafes = data;

	}

 	//const navermaps = window.naver.maps; // 혹은 withNavermaps hoc을 사용
 	//const latlng = new navermaps.LatLng(37.3595704, 127.105399)
 	return (
		<div>
			<button onClick={refetch}>Refetch</button>
			<h2>{loading && "Loading" }</h2>
			<NaverMap
				mapDivId={'maps-getting-started-uncontrolled'} // default: react-naver-map
				style={{
					width: '100%', // 네이버지도 가로 길이
					height: '85vh' // 네이버지도 세로 길이
				}}
				defaultCenter={{ lat: 37.554722, lng: 126.970833 }} // 지도 초기 위치
				defaultZoom={13} // 지도 초기 확대 배율
			>
				{ cafes  && cafes.map((cafe, index) => (
					<CafeMarker lat={cafe.lat} lng={cafe.lon} key={index}/>
				))}

			</NaverMap>
		</div>
	);
}

export default Map;
