import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { NaverMap } from 'react-naver-maps'; // 패키지 불러오기
import CafeMarker from './Cafemarker';

const useAxios = (url) => {
	const [state, setState] = useState({
		data: null,
		loading: true,
		error: null
	});

	const [trigger, setTrigger] = useState(0);

	const refetch = () => { 
		setState({
			loading: true
		});
		setTrigger(Date.now());
	};

	const getData = async () => {
		const result = await axios.post(url);
		setState({
			data: result.data,
			loading: false,
			error: null
		});
		if (result.error) {
			setState({data:result.data, loading:false, error:result.error});
		}
	};

	useEffect(() => { getData(); }, [trigger]);
	if (!url) {
		return;
	}
	return {state, refetch};
};



const Map = () => {
	const { state, refetch } = useAxios("/getcafes");
	const {data, loading, error} = state;

	let mapRef, mapBounds;
	let [center, setCenter] = useState({
		center: null,
		mapBounds: null
	});
	let [zoom, setZoom] = useState(0);

	useEffect(() => { 
		setCenter({
			mapBounds: mapRef.getBounds()
		});
		//console.log(mapBounds);
	 }, [center]);

	const cafes = data;
	let render_cafes = [];

	if (cafes) { 
  	let d = new Date();
    let day_of_week = d.getDay(); // sunday : 0, mon : 1, ...
    let cur_hour = d.getHours();
		for (let i = 0; i < cafes.length; i ++) {
			// 마커 이미지를 생성합니다

			const regex = /[\[\]]/;
			let arr = cafes[i].busy.split(regex);
			let filtered = arr.filter(function (el) {
				return el != "" && el != ", ";
			});

			let cur_busy = filtered[day_of_week].split(', ')[cur_hour];

			let cur_busy_color; 
			if (cur_busy >= 80) {
			// red (Busy : 80-100)
				cur_busy_color = "red"; 
			} else if (cur_busy >= 40) {
			// yellow (Busy : 40-59)
				cur_busy_color = "yellow"; 
			} else if (cur_busy >= 0) {
			// green (Busy : 0-39)
				cur_busy_color = "green"; 
			} else if (cur_busy == -1) {
			// grey (Busy : not_opened)
				cur_busy_color = "grey"; 
			} else {
			//예외처리
				console.log("it is wrong busy data");
				continue;
			}
			cafes[i]['cur_busy_color'] = cur_busy_color;
			cafes[i]['cur_busy'] = cur_busy.toString();

 			const navermaps = window.naver.maps; // 혹은 withNavermaps hoc을 사용

			if (center && center.mapBounds.hasLatLng(navermaps.LatLng(cafes[i].lat,cafes[i].lon))){
				//render_cafes.append(cafes[i]);
				console.log(cafes[i]);
			}
		}
	}

 	//const latlng = new navermaps.LatLng(37.3595704, 127.105399)
 	return (
		<div>
			<button onClick={refetch}>Refetch</button>
			<h2>{loading && "Loading" }</h2>
			<NaverMap
				mapDivId={'maps-getting-started-uncontrolled'} // default: react-naver-map
				naverRef={ref => { mapRef = ref }}
				style={{
					width: '100%', // 네이버지도 가로 길이
					height: '85vh' // 네이버지도 세로 길이
				}}
				defaultCenter={{ lat: 37.554722, lng: 126.970833 }} // 지도 초기 위치
				defaultZoom={13} // 지도 초기 확대 배율
				onCenterChanged={center_pos => {setCenter({center:center_pos}); } }
				onZoomChanged={zoomListener => { setZoom(zoomListener) } }
			>
				{ cafes  && cafes.map((cafe, index) => (

					<CafeMarker lat={cafe.lat} lng={cafe.lon} busy={cafe.cur_busy} busy_color={cafe.cur_busy_color} key={index}/>
				))}

			</NaverMap>
		</div>
	);
}

export default Map;
