import React, {useState, useEffect} from 'react';
import '../App.css';
import Map from './Map'; 
import { RenderAfterNavermapsLoaded} from 'react-naver-maps'; // 패키지 불러오기
import { getCurrentLocation } from '../utils/utils';

import path from 'path';
import dotenv from 'dotenv';

import Search from './Search';

import { useQuery } from "react-apollo-hooks";
import { gql } from "apollo-boost";


//dotenv.config({ path: path.join(__dirname,'/.env') });
dotenv.config();


const GET_MARKER_STATUS = gql`
    query getMarkerStatus {
        getMarkerStatus @client 
    }
`;


const App = () => {


		//console.log('i am rendering');
        const [markerStatus, setMarkerStatus] = useState("text");

        // const {data: markerStatusQuery} = useQuery(GET_MARKER_STATUS);


        // console.log(markerStatusQuery);
        // if (markerStatusQuery) {
        //     let markerStatus2 = markerStatusQuery.getMarkerStatus;
        //     console.log(markerStatus2);
        // }

        useEffect(() => {
        },[markerStatus]);

		return (
			<RenderAfterNavermapsLoaded
				ncpClientId={process.env.REACT_APP_NAVERMAP_CLIENT_KEY} // 자신의 네이버 계정에서 발급받은 Client ID
				error={<p>Maps Load Error</p>}
				loading={<p>Maps Loading...</p>}
			>
				<div id="container">
					<div className="menu_div">
						<img src="/images/ui/menu/hamburger.svg" className="menu_hamburger" alt="menu"></img>
						<img src="/images/ui/menu/activate.svg" className="menu_activate" alt="menu"></img>
				</div>

				<Map markerStatus={markerStatus}/>

                <Search setMarkerStatus={setMarkerStatus}/>

				</div>
			</RenderAfterNavermapsLoaded>
		);

}

export default App;
