export const defaults = {
    isLoggedIn: Boolean(localStorage.getItem("token")) || false,
    isPercentMarker: localStorage.getItem("markerStatus") || "text"
};


export const resolvers = {

    Mutation: {
        logUserIn: (_, {token}, {cache}) => {
            localStorage.setItem("token",token);
            cache.writeData({
                data: {
                    isLoggedIn: true
                }
            });
            return null;
        },
        logUserOut: (_, __, {cache}) => {
            localStorage.removeItem("token");
            window.location.reload();
            return null;
        },

        Check: (_, __, {cache}) => {
            localStorage.removeItem("token");
            window.location.reload();
            return null;
        },

        toggleMarkerStatus: (_,  __, {cache}) => {
            // localStorage.removeItem("percentMarker");
            if (localStorage.getItem("markerStatus") === "percent") {
                let markerStatus = "text";

            } else if (ocalStorage.getItem("markerStatus") === "text") {
                let markerStatus = "percent";

            }
            localStorage.setItem("markerStatus",markerStatus);

            cache.writeData({
                data: {
                    MarkerStatus: markerStatus
                }
            });
            return null;
        }
    }
};
