export const defaults = {
    isLoggedIn: Boolean(localStorage.getItem("token")) || false,
    isPercentMarker: Boolean(localStorage.getItem("percentMarker")) || false
};


export const resolvers = {

    Mutation: {
        logUserIn: (_, {token}, {cache}) => {
            localStorage.setItem("token",token);
            cache.writeData({
                data: {
                    isLoggedIn: true
                }
            });
            return null;
        },
        logUserOut: (_, __, {cache}) => {
            localStorage.removeItem("token");
            window.location.reload();
            return null;
        },

        Check: (_, __, {cache}) => {
            localStorage.removeItem("token");
            window.location.reload();
            return null;
        },

        MarkerToPercent: (_, {markerStatus}, {cache}) => {
            localStorage.setItem("percentMarker",markerStatus);
            cache.writeData({
                data: {
                    isPercentMarker: false
                }
            });
            return null;
        },

        MarkerToText: (_, __, {cache}) => {
            localStorage.removeItem("percentMarker");

            return null;
        }
    }
};
