import React from 'react';
import { RenderAfterNavermapsLoaded, NaverMap } from 'react-naver-maps'; // 패키지 불러오기


function NaverMapAPI() {
  return (
    <NaverMap
      mapDivId={'maps-getting-started-uncontrolled'} // default: react-naver-map
      style={{
        width: '100%', // 네이버지도 가로 길이
        height: '85vh' // 네이버지도 세로 길이
      }}
      defaultCenter={{ lat: 37.554722, lng: 126.970833 }} // 지도 초기 위치
      defaultZoom={13} // 지도 초기 확대 배율
    />
  );
}



class Map extends React.Component {
	constructor(props) {
		this.state.mapOptions = {
				center: new naver.maps.LatLng(37.3595704, 127.105399),
				zoom: 10
		};
		this.state.map = new naver.maps.Map('map', mapOptions);

	}


	state = {
		mapOptions: {},
		map: 0
	}

	render() {
  	return (
			<RenderAfterNavermapsLoaded
				ncpClientId={'SvEq3Xa03V5KvD1AFpeFlM0YAxJ9NRvBvl1WdPCB'} // 자신의 네이버 계정에서 발급받은 Client ID
				error={<p>Maps Load Error</p>}
				loading={<p>Maps Loading...</p>}
			>
				<NaverMapAPI />
			</RenderAfterNavermapsLoaded>
		);
	}
}

export default Map;
