import React from 'react';
import PropTypes from 'prop-types';
import { Marker } from 'react-naver-maps'; // 패키지 불러오기
import './marker.css';


const CustomMarker = (marker_class) => {
	const navermaps = window.naver.maps;

	return (
		{
        content:[ 
						'<div class="map_marker_div">',
							'<div class={marker_class}>',
								'18',
							'</div>',
						'</div>'].join(''),

        size: new navermaps.Size(48, 48),
        anchor: new navermaps.Point(11, 35)
    }
	)
}

const CafeMarker = ({lat, lng, busy}) => {
	console.log(busy);
	let title, marker_class;
	if (busy=='red') {
		title = 'red_marker';
		marker_class = 'busy_red';
	}
	else if (busy=='yellow') {
		title = 'yellow_marker';
		marker_class = 'busy_yellow';
	}
	else if (busy=='green') {
		title = 'green_marker';
		marker_class = 'busy_green';
	}

	else if (busy=='grey') {
		title = 'grey_marker';
		marker_class = 'busy_grey';
	}

	return (  
		<Marker
			position={{ lat: lat, lng: lng }}
			icon={CustomMarker(marker_class)}
			title= {title}
		/>
	)
}


CafeMarker.propTypes = {
	lat: PropTypes.number.isRequired,
	lng: PropTypes.number.isRequired,
	busy: PropTypes.string.isRequired
}

export default CafeMarker;
